La liste des users

<mat-card class="example-card" *ngFor=" let user of userList$ | async">
  <mat-card-header>
    <mat-card-title>Nom:{{user.firstname}}  </mat-card-title>
    <mat-card-subtitle>Prix:{{user.lastname}} </mat-card-subtitle>
    <mat-card-subtitle>Quantity:{{user.address}}</mat-card-subtitle>

  </mat-card-header>

</mat-card>
import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { user } from '../models/user';
import { UserService } from '../user.service';

@Component({
  selector: 'app-user-list',
  templateUrl: './user-list.component.html',
  styleUrls: ['./user-list.component.css']
})
export class UserListComponent implements OnInit {
  userList$: Observable<user[]>;

  constructor(private userService: UserService) {

  }

  ngOnInit() {
    this.userList$=this.userService.getUser();
  }

}
